mapper.select:
select * from t_material where status = 1

  and (name like #{condition} 
  or supplierName like #{condition}
  or specification1 like #{condition}
  or specification2 like #{condition}
  or specification3 like #{condition})

  order by supplierName, modifydate desc
  limit #{pageOffset},#{pageSize}

select * from t_material where status = 1

  and (name like #{condition}
  or specification1 like #{condition}
  or specification2 like #{condition}
  or specification3 like #{condition})

  order by modifydate desc

