mapper.select:
select customerName, sum(debt) as debt, sum(payment) as payment from T_ACCOUNT_PAYMENT 
 where month like #{month}  
 group by supplierName

mapper.update:
    update T_ACCOUNT_PAYMENT set 
      debt = debt + #{debt}
where supplierName = #{supplierName} 
  and month = #{month}

    update T_ACCOUNT_PAYMENT set 
      payment = payment + #{payment}
where supplierName = #{supplierName} 
  and month = #{month}
