mapper.select:
select * from t_transaction where status = 1

  and (orderidentify like #{condition} 
    or tranUser like #{condition} 
    or tranAccountNo like #{condition})



  and tranDate >= #{startDate}


  and tranDate <= #{endDate}

  order by modifydate desc
  limit #{pageOffset},#{pageSize}

select count(1) from t_transaction where status = 1

  and (orderidentify like #{condition} 
    or tranUser like #{condition} 
    or tranAccountNo like #{condition})



  and tranDate >= #{startDate}


  and tranDate <= #{endDate}

select * from t_transaction where status = 1

  and tranUser like #{tranUser} 



  and tranDate >= #{startDate}


  and tranDate <= #{endDate}

  order by modifydate desc
  limit #{pageOffset},#{pageSize}

select count(1) from t_transaction where status = 1

  and tranUser like #{tranUser} 



  and tranDate >= #{startDate}


  and tranDate <= #{endDate}

select * from t_transaction 
 where status = 1
   and orderIdentify = #{orderIdentify}

select * from t_transaction 
 where status = 1
   and purchaseId = #{purchaseId}
