mapper.select:
select m.*,
       p.id as p_id,
       p.name as p_name,
       p.code as p_code
  from tbl_fin_subject m 
  left join tbl_fin_subject p
    on m.parentId = p.id      
 where m.status = '1'

  and m.name like #{subName} 


  and m.code like #{subCode} 


  and m.category = #{subCategory} 


  and m.remark like #{remark} 

  order by m.code
