mapper.select:
select v.*, 
       ve.id as e_id, 
       ve.summary,
       ve.debitAmount,
       ve.creditAmount,
       ve.remark as e_remark,
       sub.id as sub_id,
       sub.code as sub_code,
       sub.name as sub_name,
       sub.fullName as sub_fullName,
       sub.category as sub_category
  from TBL_FIN_VOUCHER v, 
       TBL_FIN_VOUCHER_ENTRY ve,
       TBL_FIN_SUBJECT sub             
 where v.status = '1'
   and ve.status = '1'
   and v.id = ve.voucherId
   and ve.subjectId = sub.id

  and v.word = #{voucherWord} 


  and v.number >= #{startVouchernumber} 


  and v.number <= #{endVouchernumber} 


  and v.billStatus = #{billStatus} 


  and v.preparedBy = #{preparedBy} 


  and v.auditor like #{auditor}


  and v.billDate >= #{startDate} 


  and v.billDate <= #{endDate} 


  and v.remark like #{remark} 


  and ve.summary like #{voucherSummary} 


  and sub.code like #{subCode} 


  and sub.name like #{subName} 

  order by v.word, v.number, ve.id
  limit #{pageOffset},#{pageSize}

        select count(1) from(
	        select distinct fullRec.id from(
		        select v.*, 
		               ve.id as e_id, 
		               ve.summary,
		               ve.debitAmount,
		               ve.creditAmount,
		               ve.remark as e_remark,
		               sub.id as sub_id,
		               sub.code as sub_code,
		               sub.name as sub_name,
		               sub.fullName as sub_fullName,
		               sub.category as sub_category
		          from TBL_FIN_VOUCHER v, 
		               TBL_FIN_VOUCHER_ENTRY ve,
		               TBL_FIN_SUBJECT sub             
		         where v.status = '1'
		           and ve.status = '1'
		           and v.id = ve.voucherId
		           and ve.subjectId = sub.id

		          and v.word = #{voucherWord} 


		          and v.number >= #{startVouchernumber} 


		          and v.number <= #{endVouchernumber} 


		          and v.billStatus = #{billStatus} 


		          and v.preparedBy = #{preparedBy} 


		          and v.auditor like #{auditor}


		          and v.billDate >= #{startDate} 


		          and v.billDate <= #{endDate} 


		          and v.remark like #{remark} 


		          and ve.summary like #{voucherSummary} 


		          and sub.code like #{subCode} 


		          and sub.name like #{subName} 

	          ) fullRec
	      )disRec

select v.*, 
       ve.id as e_id, 
       ve.summary,
       ve.debitAmount,
       ve.creditAmount,
       ve.remark as e_remark,
       sub.id as sub_id,
       sub.code as sub_code,
       sub.name as sub_name,
       sub.fullName as sub_fullName,
       sub.category as sub_category
  from TBL_FIN_VOUCHER v, 
       TBL_FIN_VOUCHER_ENTRY ve,
       TBL_FIN_SUBJECT sub             
 where v.status = '1'
   and ve.status = '1'
   and v.id = ve.voucherId
   and ve.subjectId = sub.id

  and v.word = #{voucherWord} 


  and v.number >= #{startVouchernumber} 


  and v.number <= #{endVouchernumber} 


  and v.billStatus = #{billStatus} 


  and v.preparedBy = #{preparedBy} 


  and v.auditor like #{auditor}


  and v.billDate >= #{startDate} 


  and v.billDate <= #{endDate} 


  and v.remark like #{remark} 


  and ve.summary like #{voucherSummary} 


  and sub.code like #{subCode} 


  and sub.name like #{subName} 

  order by v.word, v.number, ve.id
