  and customerName like #{customerName}           



  and invoiceDate >= #{startDate}


  and invoiceDate <= #{endDate}

  order by modifydate desc

select count(1) from t_invoice where status = 1

  and customerName like #{customerName}           



  and invoiceDate >= #{startDate}


