mapper.select:
select * from t_Order where status = 1

  and (identify like #{condition}
    or custName like #{condition})


  and orderDate >= #{startDate}


  and orderDate <= #{endDate}

  order by orderDate desc
  limit #{pageOffset},#{pageSize}

select * from t_Order where status = 1

  and (identify like #{condition}
    or custName like #{condition})


  and orderType = #{orderType}


  and state = #{state}


  and orderDate >= #{startDate}


  and orderDate <= #{endDate}

  order by orderDate desc


select * from t_order left join t_orderdetail on t_order.identify = t_orderdetail.orderidentify where t_Order.status = 1

  and (identify like #{condition}
    or custName like #{condition})


  and orderType = #{orderType}


  and state = #{state}


  and orderDate >= #{startDate}


  and orderDate <= #{endDate}

  order by t_order.orderDate desc

select count(1) from t_Order where status = 1

  and (identify like #{condition}
    or custName like #{condition})


  and orderType = #{orderType}


  and state = #{state}


  and orderDate >= #{startDate}


  and orderDate <= #{endDate}
