mapper.select:
	select * from T_RECEPTION where status = 1

      and (orderIdentify like #{condition}
        or supplierName like #{condition}
        or supplierName like #{condition})


      and receiveDate >= #{startDate}


      and receiveDate <= #{endDate}

      order by modifydate desc
      limit #{pageOffset},#{pageSize}

	select * from T_RECEPTION where status = 1

      and (orderIdentify like #{condition}
        or supplierName like #{condition}
        or supplierName like #{condition})


      and receiveDate >= #{startDate}


      and receiveDate <= #{endDate}

	select count(1) from T_RECEPTION where status = 1

      and (orderIdentify like #{condition}
        or supplierName like #{condition}
        or supplierName like #{condition})


      and receiveDate >= #{startDate}


      and receiveDate <= #{endDate}

	select * from T_RECEPTION where status = 1

      and supplierName like #{supplierName}


      and receiveDate >= #{startDate}


      and receiveDate <= #{endDate}

      order by modifydate desc
      limit #{pageOffset},#{pageSize}

	select count(1) from T_RECEPTION where status = 1

      and supplierName like #{supplierName}


      and receiveDate >= #{startDate}


      and receiveDate <= #{endDate}
