mapper.select:
select * from t_productin where status = 1

  and (pdtNo like #{condition}
    or orderIdentify like #{condition})             


  and storageDate >= #{startDate}


  and storageDate <= #{endDate}

  order by modifydate desc
  limit #{pageOffset},#{pageSize}

select * from t_productin where status = 1

  and (pdtNo like #{condition}
    or orderIdentify like #{condition})             


  and storageDate >= #{startDate}


  and storageDate <= #{endDate}

  order by modifydate desc

select count(1) from t_productin where status = 1

  and (pdtNo like #{condition}
    or orderIdentify like #{condition})             


  and storageDate >= #{startDate}


  and storageDate <= #{endDate}

select t1.pdtNo, t1.content, t1.amount - (case when t2.amount is null then 0 else t2.amount end) as amount from (select pdtNo, content, sum(amount) as amount from t_productin where status = 1 group by pdtNo, content order by modifydate desc ) t1 left join (select pdtNo, content, sum(amount) as amount from t_productin_deliver where status = 1 group by pdtNo, content order by modifydate desc ) t2 on t1.pdtNo = t2.pdtNo and t1.content = t2.content where 1 = 1
and t1.pdtNo like #{pdtNo}
limit #{pageOffset},#{pageSize}

select count(1) from (
select pdtNo, content, sum(amount) from t_productin where status = 1

  and pdtNo like #{pdtNo}           


  and storageDate >= #{startDate}


  and storageDate <= #{endDate}

  group by pdtNo, content) tmp
