mapper.select:
select m.id as m_id, 
       m.name as m_name, 
       m.specification as m_specification, 
       m.unit as m_unit, 
       m.storage as m_storage, 
       m.remark as m_remark, 
       m.status as m_status, 
       c.id as c_id,
       c.name as c_name,
       c.code as c_code 
  from tbl_material m, 
       tbl_material_category c             
 where m.status = '1'
   and m.categoryId = c.id

  and m.name like #{name} 


  and c.code like #{ctgCode} 


  and m.specification like #{specification} 


  and m.storage >= #{lowerStorage} 


  and m.storage <= #{upperStorage} 


  and m.remark like #{remark} 

  order by c.code, m.name desc
  limit #{pageOffset},#{pageSize}

select m.id as m_id, 
       m.name as m_name, 
       m.specification as m_specification, 
       m.unit as m_unit, 
       m.storage as m_storage, 
       m.remark as m_remark, 
       m.status as m_status, 
       c.id as c_id,
       c.name as c_name,
       c.code as c_code 
  from tbl_material m, 
       tbl_material_category c           
 where m.status = '1'
   and m.categoryId = c.id

  and m.name like #{name} 


  and c.code like #{ctgCode} 


  and m.specification like #{specification} 


  and m.storage >= #{lowerStorage} 


  and m.storage <= #{upperStorage} 


  and m.remark like #{remark} 

  order by c.code, m.name desc

select count(1) from tbl_material m, 
                      tbl_material_category c             
 where m.status = '1'
   and m.categoryId = c.id

  and m.name like #{name} 


  and c.code like #{ctgCode} 


  and m.specification like #{specification} 


  and m.storage >= #{lowerStorage} 


  and m.storage <= #{upperStorage} 


  and m.remark like #{remark}
